var openMenuButton=document.querySelector(".menu-button"),closeMenuButton=document.querySelector(".menu-close"),menuLinks=document.querySelectorAll(".navigation__link");function addBodyClass(n){document.body.classList.add(n)}function removeBodyClass(n){document.body.classList.remove(n)}function openNav(n,e){n.addEventListener("click",function(){addBodyClass(e)})}function closeNav(n,e){n.addEventListener("click",function(){removeBodyClass(e)})}function toggleNav(){openNav(openMenuButton,"open-nav"),closeNav(closeMenuButton,"open-nav")}function closeNavOnScroll(n,e){n.forEach(function(n){n.addEventListener("click",function(){removeBodyClass(e)})})}toggleNav(),closeNavOnScroll(menuLinks,"open-nav");